    attackdamageperlevel, attackspeedperlevel, attackspeed, passive
) VALUES (
    'Aphelios', 523, 'Aphelios', 'Arme des Lunaris', 
    'Émergeant des ombres au clair de lune, Aphelios abat ceux qui voudraient anéantir sa foi sans un mot ; ses armes et sa précision mortelle parlent pour lui. Un poison qui le rend muet coule dans ses veines, mais il est constamment guidé par sa sœur, Alune. Depuis son temple lointain, elle lui confère un arsenal d''armes en pierre de lune. Tant que la lune brillera dans le ciel, Aphelios ne sera jamais seul.', 'Émergeant des ombres au clair de lune, Aphelios abat ceux qui voudraient anéantir sa foi sans un mot ; ses armes et sa précision mortelle parlent pour lui. Un poison qui le rend muet coule dans ses veines, mais il est constamment guidé par sa sœur...', '["Chacune des armes d''Aphelios a ses propres forces. Essayez de trouver la situation parfaite pour vos armes actuelles. "]', '["Chacune des armes d''Aphelios a ses propres faiblesses. Essayez d''exploiter celles qui b\u00e9n\u00e9ficieront le plus \u00e0 votre champion. Attention \u00e0 l''arme gravitationnelle violette\u00a0! Elle peut vous immobiliser."]', 
    'Aphelios.png', '{"Marksman"}', 'Mana', 
    6, 2, 1, 10,
    600, 102, 348, 42,
    325, 26, 4.2,
    30, 1.3, 550,
    3.25, 0.55, 6.5, 0.4,
    0, 0, 55, 2.3,
    2.1, 0.665, '{"name": "Le Tueur et l''Oracle", "description": "Aphelios manie 5 armes de Lunari forg\u00e9es par sa s\u0153ur Alune. Il peut en porter deux \u00e0 la fois\u00a0: une principale et une secondaire. Chaque arme poss\u00e8de une attaque de base et une comp\u00e9tence uniques. Les attaques et les comp\u00e9tences consomment les munitions de son arme. Lorsqu''il n''a plus de munitions, Aphelios jette son arme et Alune invoque la suivante (parmi les 5). ", "image": {"full": "ApheliosP.png", "sprite": "passive0.png", "group": "passive", "x": 432, "y": 0, "w": 48, "h": 48}}'
);
