INSERT INTO champion (
    riot_id, riot_key, name, title, lore, blurb, allytips, enemytips, 
    image_icone, tags, partype, attack, defense, magic, difficulty,
    hp, hpperlevel, mp, mpperlevel, movespeed, armor, armorperlevel,
    spellblock, spellblockperlevel, attackrange, hpregen, hpregenperlevel,
    mpregen, mpregenperlevel, crit, critperlevel, attackdamage, 
    attackdamageperlevel, attackspeedperlevel, attackspeed, passive
) VALUES (
    'Ekko', 245, 'Ekko', 'Fractureur du temps', 
    'Jeune prodige des rues mal famées de Zaun, Ekko est capable de manipuler le temps pour renverser toute situation dangereuse à son avantage. Il utilise sa propre invention, la clepsydre-zéro, pour explorer les différents possibles de la réalité, sculptant ainsi des moments parfaits qui donnent l''impression qu''il réussit l''impossible du premier coup, à chaque fois. Bien qu''Ekko aime sa liberté, lorsque ses proches sont menacés, lui et les Feux volants sont prêts à tout pour les défendre.', 'Jeune prodige des rues mal famées de Zaun, Ekko est capable de manipuler le temps pour renverser toute situation dangereuse à son avantage. Il utilise sa propre invention, la clepsydre-zéro, pour explorer les différents possibles de la réalité...', '["Chronofracture est un excellent outil de fuite, mais il peut \u00e9galement \u00eatre redoutable en attaque. Ne sous-estimez pas les d\u00e9g\u00e2ts qu''il peut infliger.", "Si vous arrivez \u00e0 d\u00e9clencher R\u00e9Zonance sur un champion ennemi, c''est un risque qui peut payer. Le bonus en vitesse de d\u00e9placement permet ensuite de s''\u00e9chapper facilement.", "La ru\u00e9e du Rush d\u00e9phas\u00e9 est tr\u00e8s efficace pour profiter des autres comp\u00e9tences d''Ekko. Servez-vous-en pour frapper deux fois avec R\u00e9trobang ou pour faire exploser Convergence parall\u00e8le."]', '["Ekko est nettement plus vuln\u00e9rable quand son ultime n''est pas disponible. Pour savoir s''il peut lancer Chronofracture, regardez bien s''il laisse une tra\u00een\u00e9e derri\u00e8re lui.", "La zone d''\u00e9tourdissement d''Ekko met 3 secondes \u00e0 s''enclencher. Surveillez l''image qu''il produit au moment du lancer et essayez de deviner o\u00f9 la zone est plac\u00e9e.", "Le deuxi\u00e8me coup de R\u00e9trobang inflige plus de d\u00e9g\u00e2ts que le premier, alors essayez de l''\u00e9viter."]', 
    'Ekko.png', '{"Assassin","Mage"}', 'Mana', 
    5, 3, 7, 8,
    655, 99, 280, 70,
    340, 32, 4.2,
    32, 2.05, 125,
    9, 0.9, 7, 0.8,
    0, 0, 58, 3,
    3.3, 0.688, '{"name": "R\u00e9Zonance", "description": "Toutes les trois attaques ou comp\u00e9tences infligeant des d\u00e9g\u00e2ts sur une m\u00eame cible, Ekko inflige des d\u00e9g\u00e2ts magiques suppl\u00e9mentaires. Si la cible est un champion, il gagne en plus un bref bonus en vitesse de d\u00e9placement.<br><br>", "image": {"full": "Ekko_P.png", "sprite": "passive0.png", "group": "passive", "x": 432, "y": 96, "w": 48, "h": 48}}'
);
