INSERT INTO champion (
    riot_id, riot_key, name, title, lore, blurb, allytips, enemytips, 
    image_icone, tags, partype, attack, defense, magic, difficulty,
    hp, hpperlevel, mp, mpperlevel, movespeed, armor, armorperlevel,
    spellblock, spellblockperlevel, attackrange, hpregen, hpregenperlevel,
    mpregen, mpregenperlevel, crit, critperlevel, attackdamage, 
    attackdamageperlevel, attackspeedperlevel, attackspeed, passive
) VALUES (
    'Jax', 24, 'Jax', 'Maître d''armes', 
    'Jax, dont nul ne peut égaler l''art du sarcasme et des armements inhabituels, est connu comme le dernier maître d''armes d''Icathia. Après la chute de sa terre natale, ravagée par le Néant qu''elle avait eu l''orgueil de déchaîner, Jax et ses compagnons jurèrent de protéger le peu qu''il en restait. La magie montant de nouveau dans le monde, la menace se fait plus pressante et Jax parcourt Valoran, porteur des dernières lumières d''Icathia. Il éprouve les qualités de tous les guerriers qu''il rencontre pour voir s''ils sont assez puissants pour combattre à ses côtés...', 'Jax, dont nul ne peut égaler l''art du sarcasme et des armements inhabituels, est connu comme le dernier maître d''armes d''Icathia. Après la chute de sa terre natale, ravagée par le Néant qu''elle avait eu l''orgueil de déchaîner, Jax et ses compagnons...', '["Jax peut utiliser Frappe bondissante pour rejoindre des alli\u00e9s, y compris des balises. Vous pouvez l''utiliser pour fuir.", "Jax profite grandement des objets augmentant \u00e0 la fois les d\u00e9g\u00e2ts d''attaque et la puissance, tels que Lame enrag\u00e9e de Guinsoo et Pistolame Hextech."]', '["Essayez d''engager le combat avec de courtes attaques au lieu de l''affronter en force. L''emp\u00eacher de cumuler les attaques successives r\u00e9duit le montant de d\u00e9g\u00e2ts qu''il inflige.", "Jax peut esquiver toutes les attaques pendant un tr\u00e8s bref instant et, tout de suite apr\u00e8s, \u00e9tourdir les ennemis au contact. Attendez la fin de ses esquives pour attaquer."]', 
    'Jax.png', '{"Fighter"}', 'Mana', 
    7, 5, 7, 5,
    650, 103, 339, 52,
    350, 36, 4.2,
    32, 2.05, 125,
    8.5, 0.55, 8.2, 0.7,
    0, 0, 68, 4.25,
    3.4, 0.638, '{"name": "Acharnement", "description": "Les attaques de base successives de Jax augmentent continuellement sa vitesse d''attaque.", "image": {"full": "Armsmaster_MasterOfArms.png", "sprite": "passive1.png", "group": "passive", "x": 48, "y": 96, "w": 48, "h": 48}}'
);
